/* eslint-disable no-mixed-spaces-and-tabs */
// Libs
import React, { Component } from "react";
import { connect } from "react-redux";
import styled from "styled-components";

// Components
import Button from "../../../components/Button";
import Input from "../../../components/Input";
import ImageLogo from "../../../components/ImageLogo";
import FisicalPersonSucess from "./FisicalPersonSucess";
import FisicalPersonTerms from "./FisicalPersonTerms";
import { Container } from "./CreateFisicalPersonScreen";

// Images
import VisibilityOff from "../../../assets/visibility-off.svg";
import VisibilityOn from "../../../assets/visibility-on.svg";

// Redux
import { addNewUser } from "../../../dataflow/modules/sign-up-modules";
import ModalTerms from "./FisicalPersonTerms";

const mapStateToProps = state => ({
  signup: state.signup
});

const mapDispatchToProps = dispatch => ({
  addNewUser: user => {
    dispatch(addNewUser(user));
  }
});

export const Form = styled.form`
  min-width: 35%;
  min-height: 100vh;
  background-color: #fff;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 1rem 0;

  @media (max-width: 500px) {
    padding: 0 2rem;
  }

  h1 {
    font-size: 1.4rem;
    font-family: "Overpass", ExtraBold;
    text-transform: uppercase;
    margin: 2rem 0 1.5rem 2.5rem;
    align-self: flex-start;
  }
  @media (max-width: 425px) {
    h1 {
      margin: 2rem 0;
      font-size: 1.3rem;
    }
  }

  @media (max-width: 375px) {
    padding: 1rem;
  }

  span {
    width: 90%;
    display: flex;
    justify-content: space-between;
    position: relative;
  }
  @media (max-width: 425px) {
    span {
      width: 100%;
      flex-direction: column;
    }
    p {
      text-align: center;
    }
  }
`;

export const Label = styled.label`
  position: relative;
  width: 90%;
  display: flex;
  align-items: center;
  flex-direction: column;

  @media (max-width: 425px) {
    width: 100%;
  }
  p {
    align-self: flex-start;
    text-transform: uppercase;
    color: #85144b;
    font-size: 0.7rem;
    margin: 1rem 1.3rem;
    font-family: Overpass, Bold;
    font-weight: bold;
  }

  @media (max-width: 425px) {
    p {
      text-align: left;
      margin-left: 0.2rem;
    }
  }
`;

export const Error = styled.h6`
  font-size: 0.6rem;
  color: red;
  align-self: flex-start;
  font-weight: normal;
  margin-left: 1.4rem;

  @media (max-width: 425px) {
    margin: 0;
  }
`;
export const InputForm = styled.input`
  width: 90%;
  height: 40px;
  border: 1px solid #ffcfcd;
  background: #fafafa;
  padding: 0.5rem;
  font-size: 0.9rem;

  @media (max-width: 425px) {
    width: 100%;
  }
`;

export const ImagePassword = styled.img`
  position: absolute;
  bottom: 1.5vh;
  right: 0.7rem;
  cursor: pointer;
`;
export const TextTerms = styled.p`
  font-size: 0.6rem;
  margin: 1rem 0;
  color: #505050;
  font-family: Overpass, Regular;
  text-align: center;

  strong {
    color: #85144b;
    font-size: 0.7rem;
    cursor: pointer;
    text-decoration: underline;
    margin: 0 0.2rem;
    font-family: Overpass, Regular;
  }
`;

class FisicalPersonForm extends Component {
  state = {
    togglePassword: false,
    modalSucess: false,
    user: {
      nome: "",
      sobrenome: "",
      rg: "",
      orgao: "",
      uf: "",
      nascimento: "",
      cpf: "",
      email: "",
      telefone: "",
      senha: ""
    },
    isErrorRg: false,
    isErrorPassword: false,
    isErrorCpf: false,
    isEmpty: false,
    modalTerms: false
  };

  togglePassword = ev => {
    ev.preventDefault();
    this.setState({
      togglePassword: !this.state.togglePassword
    });
  };

  handleModalSucess = () => {
    this.setState({
      modalSucess: !this.state.modalSucess
    });
  };

  handleChange = (field, ev) => {
    const { user } = this.state;
    user[field] = ev.target.value;
    this.setState({ user });
  };

  handleSubmit = ev => {
    ev.preventDefault();
    this.errors();
  };

  errors = () => {
    const { user } = this.state;
    const {
      nome,
      sobrenome,
      rg,
      orgao,
      uf,
      nascimento,
      cpf,
      email,
      telefone,
      senha
    } = this.state.user;

    if (senha.length < 4) {
      this.setState({ isErrorPassword: true });
    } else {
      this.setState({ isErrorPassword: false });
    }
    if (rg.length < 9 || rg.length > 9) {
      this.setState({ isErrorRg: true });
    } else {
      this.setState({ isErrorRg: false });
    }
    if (cpf.length < 11 || cpf.length > 11) {
      this.setState({ isErrorCpf: true });
    } else {
      this.setState({ isErrorCpf: false });
    }
    if (
      nome === "" ||
      sobrenome === "" ||
      rg === "" ||
      orgao === "" ||
      uf === "" ||
      nascimento === "" ||
      cpf === "" ||
      email === "" ||
      telefone === "" ||
      senha === ""
    ) {
      this.setState({ isEmpty: true });
    } else {
      this.setState({ isEmpty: false });
      this.props.addNewUser(user);
      this.handleModalSucess();
    }
  };

  render() {
    const errorMessage = [
      "RG inv치lido",
      "Senha fraca",
      "CPF inv치lido",
      "Preencha todos os valores"
    ];

    const {
      isErrorPassword,
      isErrorRg,
      isErrorCpf,
      modalSucess,
      isEmpty
    } = this.state;

    return (
      <>
        {modalSucess && (
          <Container>
            <FisicalPersonSucess handleModalSucess={this.handleModalSucess} />
          </Container>
        )}
      </>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(FisicalPersonForm);
