// Libs
import React, { Component } from 'react';
import styled from 'styled-components';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';

// Components
import ImageLogo from '../../../components/ImageLogo';
import Input from '../../../components/Input';
import Button from '../../../components/Button';

// Redux
import { addNewPassword } from '../../../dataflow/modules/sign-up-modules';

const mapStateToProps = state => ({
	singup: state.singup,
});

const mapDispatchToProps = dispatch => ({
	addNewPassword: (newPassword) => {
		dispatch(addNewPassword(newPassword));
	},
});


const ContainerForm = styled.div`
  min-height: 100vh;
  background-color: #FFCFCD;
	min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  margin: 0;
  @media (max-width: 648px) {
		background-color: #fff;
	}
`;

const Form = styled.form`
  width: 30%;
	border-radius: 5px;
  background-color: #fff;
  display: flex;
  align-items: center;
  flex-direction: column;
	margin: 3rem;
	border-radius: 4px;
  @media (max-width: 980px) {
		width: 40%;
	}
	@media (max-width: 786px) {
		width: 50%;
	}
	@media (max-width: 648px) {
		width: 90%;
		margin: 0;
	}
	input{
		margin: 0.3rem 0 0.6rem 0;
		border: ${props => (props.withError === true ? '1px solid #f00' : '1px solid #ffcfcd')};
	}
`;

const Span = styled.span`
	width: 80%;
  @media (max-width: 648px) {
		width: 90%;
	}
`;

const Title = styled.h1`
  color: #231F20;
  font-size: 1.25rem;
  font-weight: 900;
  font-family: Overpass;
  margin: 2rem 0 1.5rem 0;
	text-transform: uppercase;
  @media (max-width: 648px) {
		display: none;
	}
`;

const Paragraph = styled.p`
  color: #231F20;
  font-family: Overpass, Regular;
  margin-bottom: 1.5rem;
	@media (max-width: 648px) {
		margin-top: 3rem;
	}
	@media (max-width: 460px) {
		font-size: 0.9rem;
		width: 90%;
	}
`;

const ErrorMessage = styled.h4`
  color: #D63434;
  display: flex;
  justify-content: flex-end;
  font-size: 0.6rem;
  font-family: Overpass, Regular;
`;

const Label = styled.label`
  color: #85144B;
  font-size: 0.8rem;
  margin: 0.9rem;
  font-family: Overpass;
  font-weight: bold;
	text-transform: uppercase;
`;

const BackLogin = styled.span` 
  display: flex;
  justify-content: center;
`;

const ButtonText = styled(Link)`
	color: #85144B;
	font-size: 1rem; 
	font-family: Overpass, Regular;
	margin-bottom: 2rem;
	text-decoration: none;
	text-transform: uppercase;
`;

class NewPasswordScreen extends Component {
	constructor(props) {
		super(props);
		this.state = {
			newPassword: '',
			newPasswordError: false,
			repeatPassword: '',
			repetPasswordError: false,
			redirect: false,
		};
	}

	handleSubmit = (ev) => {
		ev.preventDefault();
		this.handleErrors();
	}

	handleErrors = () => {
		const { newPassword, repeatPassword } = this.state;

		if (newPassword.length < 6) {
			this.setState({
				newPasswordError: true,
			});
		} else {
			this.setState({
				newPasswordError: false,
			});
		}

		if (newPassword !== repeatPassword) {
			this.setState({
				repetPasswordError: true,
			});
		} else {
			this.setState({
				repetPasswordError: false,
			});
		}

		if (newPassword.length > 5 && newPassword === repeatPassword) {
			this.props.addNewPassword({ password: newPassword });
		}
	}

	handleChangeNewPassword = (ev) => {
		this.setState({
			newPassword: ev.target.value,
		});
	}

	handleChangeRepetPassword = (ev) => {
		this.setState({
			repeatPassword: ev.target.value,
		});
	}

	handleChangeRedirect = () => {
		const { newPassword, repeatPassword } = this.state;
		if (newPassword.length > 5 && newPassword === repeatPassword) {
			this.setState({ redirect: true });
		}
	}

	render() {
		const {
			newPasswordError, repetPasswordError, redirect, newPassword, confirmationCode, repeatPassword,
		} = this.state;

		const errorMessages = [
			'Use 6 caracteres ou mais para a sua senha',
			'Essa senha não se coincidem. Tente novamente',
		];
		return (
			<ContainerForm>
				<ImageLogo margin='3rem 0 0 0' />
				<Form onSubmit={this.handleSubmit} withError={newPasswordError || repetPasswordError}>
					<Span>
						<Title>redefinição de senha</Title>
						<Paragraph>Um código de confirmação foi enviado para name@email.com, por favor, cole-o abaixo:</Paragraph>
						<Label>código de confirmação</Label>
						<Input
							login
							value={confirmationCode}
							type='text'
							placeholder="Insira aqui o código"
							required
						/>
						<Label>nova senha</Label>
						<Input
							login
							value={newPassword}
							type='password'
							onChange={ev => this.handleChangeNewPassword(ev)}
							placeholder="Insira aqui sua senha"
							required
						/>
						{newPasswordError && <ErrorMessage>{errorMessages[0]}</ErrorMessage>}
						<Label>repita nova senha</Label>
						<Input
							login
							value={repeatPassword}
							type='password'
							onChange={ev => this.handleChangeRepetPassword(ev)}
							placeholder="Repita sua senha"
							required
						/>
						{repetPasswordError && <ErrorMessage>{errorMessages[1]}</ErrorMessage>}
					</Span>
					<Button
						width='80%'
						widthMobile='90%'
						margin='1rem 0 1.5rem 0;'
						marginMobile='1rem 0 2.5rem'
						text="prossiga com nova senha"
						type="submit"
						onClick={() => this.handleChangeRedirect()}
					/>
					<BackLogin>
						<ButtonText to={'/resetcode'}>reenviar e-mail</ButtonText>
					</BackLogin>
				</Form>
				{redirect && <Redirect to={'/loginreset'} />}
			</ContainerForm>
		);
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(NewPasswordScreen);
